# Player position
player_x, player_y = 5, 5  # Startposition des Spielers in der Welt

# Player color
PLAYER_COLOR = (255, 0, 0)  # Rot

# Aktueller Blocktyp zum Platzieren
current_block_type = 1

# Game loop
running = True
while running:
    for event in pygame.event.get():
        if event.type == pygame.QUIT:
            running = False
        elif event.type == pygame.KEYDOWN:
            # Bewegung des Spielers
            if event.key == pygame.K_UP and player_y > 0:
                player_y -= 1
            elif event.key == pygame.K_DOWN and player_y < len(world) - 1:
                player_y += 1
            elif event.key == pygame.K_LEFT and player_x > 0:
                player_x -= 1
            elif event.key == pygame.K_RIGHT and player_x < len(world[0]) - 1:
                player_x += 1
            elif event.key == pygame.K_SPACE:
                # Block setzen
                world[player_y][player_x] = current_block_type
            elif event.key == pygame.K_r:
                # Block entfernen
                world[player_y][player_x] = 0
            elif event.key == pygame.K_1:
                # Wechsel zu Blocktyp 1 (Grün)
                current_block_type = 1
            elif event.key == pygame.K_2:
                # Wechsel zu Blocktyp 2 (Braun)
                current_block_type = 2

    # Clear screen
    screen.fill(SKY_COLOR)  # Himmel zeichnen

    # Draw the world
    for row_index, row in enumerate(world):
        for col_index, block in enumerate(row):
            x = col_index * BLOCK_SIZE
            y = row_index * BLOCK_SIZE + WORLD_VERTICAL_OFFSET  # Offset hinzufügen
            if block == 1:
                pygame.draw.rect(screen, GREEN, (x, y, BLOCK_SIZE, BLOCK_SIZE))
            elif block == 2:
                pygame.draw.rect(screen, BROWN, (x, y, BLOCK_SIZE, BLOCK_SIZE))
            elif block == 0:
                pygame.draw.rect(screen, GRAY, (x, y, BLOCK_SIZE, BLOCK_SIZE))

    # Draw the player
    pygame.draw.rect(
        screen,
        PLAYER_COLOR,
        (
            player_x * BLOCK_SIZE + (BLOCK_SIZE - PLAYER_WIDTH) // 2,  # Center horizontally
            player_y * BLOCK_SIZE + (BLOCK_SIZE - PLAYER_HEIGHT) // 2 + WORLD_VERTICAL_OFFSET,  # Offset hinzufügen
            PLAYER_WIDTH,
            PLAYER_HEIGHT,
        ),
    )

    # Update display
    pygame.display.flip()

# Quit Pygame
pygame.quit()
sys.exit()

